<?php
require_once('Classes/DBC2Array.Class.php');
Class SQLGenerator
{
	private static $DBCDirectory;
	private static $Locale;

	public function __construct($DBCDir, $Loc)
	{
		SQLGenerator::$DBCDirectory = $DBCDir;
		SQLGenerator::$Locale = $Loc;
	}

    public static function DBCVersion($FileName, $Format)
    {
        $File = fopen(SQLGenerator::$DBCDirectory.$FileName, "rb") or die("Cannot open file " . $FileName . "\n");
        print_r(unpack("VrecordCount/VfieldCount/VrecordSize/VstringSize", fread($File, 16)));
    }

	public static function Unpack($Filename, $Format, $MapGenerator = false)
	{
		if (@stat(SQLGenerator::$DBCDirectory . $Filename) == NULL) 
			$Filename = strtolower($Filename);
		if(!$MapGenerator)
			echo base64_decode(SQLGenerator::GetHeader($Filename));
    	return DBC2Array::Convert(SQLGenerator::$DBCDirectory . $Filename, $Format);
	}

	public static function PrintSQL($Header, $Data)
	{
		$Size = 0;
		echo "$Header\n";
		foreach($Data as $Row)
		{
			if ($Size)
			{
				if ($Size > 937420)
				{
					echo ";\n\n\n$Header\n";
					$Size = 0;
				}
				else
					echo ",\n";
			}

			foreach($Row as $i => $Value)
				if (!is_int($Value) && !is_float($Value)) $Row[$i] = "'" . str_replace("\r\n", "\\n", addslashes($Value)) . "'";
			
			$Output = "(".implode(", ", $Row).")";
			$Size += strlen($Output);
			echo $Output;
		}
		echo ";\n\n";
	}

	public static function PrintUpdate($Table, $Data, $Keys, $Values)
	{
		echo "\n";
		foreach ($Data as $Row)
		{
			$Where = array();
			foreach ($Keys as $i => $name)
				$Where[] = "`$name` = " . "'" . str_replace("\r\n", "\\n", addslashes($Row[$i])) . "'";

			$Set = array();
			foreach ($Values as $i => $name)
				$Set[] = "`$name` = " . "'" . str_replace("\r\n", "\\n", addslashes($Row[$i])) . "'";

			echo "UPDATE $Table SET " , implode(", ", $Set) , " WHERE " , implode(" AND ", $Where), ";\n";
		}
	}

	public static function Localize($Format)
	{
		$Result = $Format;
		for ($i=0; $i<strlen($Format); $i++)
			if ($Format[$i] == 'S')
			{
				$Result[$i] = 'x';
				$Result[$i+SQLGenerator::$Locale] = 's';
			}
		return $Result;
	}

	public static function RemovePath($UnpackedDBC)
	{
		foreach ($UnpackedDBC as $i => $Row) 
			$UnpackedDBC[$i][1] = substr(strrchr($UnpackedDBC[$i][1],"\\"),1);
		return $UnpackedDBC;
	}

	public static function Factionize($UnpackedDBC)
	{
		$HordeID = 1801;
		$HordeRow = array();
		foreach ($UnpackedDBC as $Row) if ($Row[0] == $HordeID) $HordeRow = $Row;
		$AllianceID = 1802;
		$AllianceRow = array();
		foreach ($UnpackedDBC as $Row) if ($Row[0] == $AllianceID) $AllianceRow = $Row;
		$FactionAlliance = array();
		$FactionHorde = array();
		foreach ($UnpackedDBC as $i => $Row)
		{
			$Alliance = 0;
			if ($Row[5]==$AllianceID || $Row[6]==$AllianceID || $Row[7]==$AllianceID || $Row[7]==$AllianceID)
				$Alliance = -1;
			else if ($Row[9]==$AllianceID || $Row[10]==$AllianceID || $Row[11]==$AllianceID || $Row[12]==$AllianceID)
				$Alliance = 1;
			else if (($Row[3] & $AllianceRow[2]) != 0 || ($Row[2] & $AllianceRow[3]) != 0)
				$Alliance = 1;
			else if (($Row[4] & $AllianceRow[2]) != 0 || ($Row[2] & $AllianceRow[4]) != 0)
				$Alliance = -1;
			$FactionAlliance[$Row[1]] = $Alliance;

			$Horde = 0;
			if ($Row[5]==$HordeID || $Row[6]==$HordeID || $Row[7]==$HordeID || $Row[7]==$HordeID)
				$Horde = -1;
			else if ($Row[9]==$HordeID || $Row[10]==$HordeID || $Row[11]==$HordeID || $Row[12]==$HordeID)
				$Horde = 1;
			else if (($Row[3] & $HordeRow[2]) != 0 || ($Row[2] & $HordeRow[3]) != 0)
				$Horde = 1;
			else if (($Row[4] & $HordeRow[2]) != 0 || ($Row[2] & $HordeRow[4]) != 0)
				$Horde = -1;
			$FactionHorde[$Row[1]] = $Horde;

			$UnpackedDBC[$i][2] = $Alliance;
			$UnpackedDBC[$i][3] = $Horde;
			unset($UnpackedDBC[$i][4]);
			unset($UnpackedDBC[$i][5]);
			unset($UnpackedDBC[$i][6]);
			unset($UnpackedDBC[$i][7]);
			unset($UnpackedDBC[$i][8]);
			unset($UnpackedDBC[$i][9]);
			unset($UnpackedDBC[$i][10]);
			unset($UnpackedDBC[$i][11]);
			unset($UnpackedDBC[$i][12]);
		}
		return $UnpackedDBC;
	}

	public static function GetSide($UnpackedDBC)
	{
		foreach ($UnpackedDBC as $i => $Row)
		{
			$UnpackedDBC[$i][4] = $UnpackedDBC[$i][3];
			$UnpackedDBC[$i][3] = 0;
			if (isset($FactionAlliance[$Row[0]]) && isset($FactionHorde[$Row[0]]))
			{
				if ($FactionAlliance[$Row[0]] == 1 && $FactionHorde[$Row[0]] == -1)
					$UnpackedDBC[$i][3] = 1;
				if ($FactionAlliance[$Row[0]] == -1 && $FactionHorde[$Row[0]] == 1)
					$UnpackedDBC[$i][3] = 2;
			}
		}
	    return $UnpackedDBC;
	}

	public static function MapGenerator($DBCArray)
	{
		$FinalDBC		=	array();
		$MapDBC			=	$DBCArray[0][0];
		$MapFormat		=	$DBCArray[0][1];
		$AreaDBC		=	$DBCArray[1][0];
		$AreaFormat		=	$DBCArray[1][1];
		$WorldMapDBC	=	$DBCArray[2][0];
		$WorldMapFormat	=	$DBCArray[2][1];

		$MapType	=	array();
		$AreaType	=	array();

		$TempDBC = SQLGenerator::Unpack($MapDBC, $MapFormat, true);
		foreach ($TempDBC as $Row)
		{
			$MapType[$Row[0]] = $Row[1];
			if ($Row[3]) $AreaType[$Row[0] . "@" . $Row[3]] = $Row[1];
		}

		$TempDBC = SQLGenerator::Unpack($AreaDBC, $AreaFormat, true);
		foreach ($TempDBC as $Row)
		{
			$Type = 0;
			if (isset($maptype[$Row[1]]))
				$Type = $maptype[$Row[1]];
			if (isset($areatype[$Row[1]."@".$Row[0]]))
				$Type = $areatype[$Row[1]."@".$Row[0]];
			$FinalDBC[$Row[0]] = array($Row[1], $Row[0], $Row[2], 0, 0, 0, 0, $Type);
		}

		$TempDBC = SQLGenerator::Unpack($WorldMapDBC, $WorldMapFormat, true);
		foreach ($TempDBC as $Row)
		{
			if (isset($FinalDBC[$Row[1]]))
			{
				$FinalDBC[$Row[1]][3] = ($Row[5]<$Row[6]) ? $Row[5] : $Row[6];
				$FinalDBC[$Row[1]][4] = ($Row[3]<$Row[4]) ? $Row[3] : $Row[4];
				$FinalDBC[$Row[1]][5] = ($Row[5]<$Row[6]) ? $Row[6] : $Row[5];
				$FinalDBC[$Row[1]][6] = ($Row[3]<$Row[4]) ? $Row[4] : $Row[3];
			}
		}
		unset($TempDBC);
		echo base64_decode(SQLGenerator::GetHeader('Group.dbc'));
		return $FinalDBC;
	}

	public static function GetHeader($DBCName)
	{
		$Headers = array(
			"Achievement_Category.dbc" => "",
			"Achievement.dbc" => "",
			"Achievement_Criteria.dbc" => "",
			"GlyphProperties.dbc" => "",
			"Item.dbc" => "",
			"CharTitles.dbc" => "",
			"ItemExtendedCost.dbc" => "",
			"SkillLineAbility.dbc" => "",
			"SkillLine.dbc" => "",
			"SpellDispelType.dbc" => "",
			"SpellMechanic.dbc" => "",
			"Resistances.dbc" => "",
			"SpellCastTimes.dbc" => "",
			"SpellIcon.dbc" => "",
			"Lock.dbc" => "",
			"ItemDisplayInfo.dbc" => "",
			"ItemSet.dbc" => "",
			"Spell.dbc" => "",
			"SpellDuration.dbc" => "",
			"SpellRange.dbc" => "",
			"SpellRadius.dbc" => "",
			"SpellItemEnchantment.dbc" => "",
			"GemProperties.dbc" => "",
			"Talent.dbc" => "",
			"TalentTab.dbc" => "",
			"FactionTemplate.dbc" => "",
			"Faction.dbc" => "",
			"Group.dbc" => "",
		);
		return $Headers[$DBCName];
	}
}

?>